<?phpnamespace App\Auth\Domain;use function App\addLog;use  App\Auth\Model\Menu as ModelMenu;/** * 规则领域类 * * @author hms */class Menu{    private static $Model = null;    public function __construct()    {        if (self::$Model == null) {            self::$Model = new ModelMenu();        }    }    /**获取规则列表     * @param $apiObj Api对象，方便多参数获取     * @return array 数据对象     * @return array 数据对象[items] 数据项     * @return int 数据对象[count] 数据总数 用于分页     */    public function getList($param)    {        $rs = array('list' => array(), 'count' => 0);        $rs['count'] = self::$Model->getCount($param['keyWord']);        $rs['list'] = self::$Model->getList($param);        return $rs;    }    public function getLimitList($param)    {        if (is_object($param)) {            $param = get_object_vars($param);        }        $rs = array('list' => array(), 'count' => 0);        $rs['count'] = self::$Model->getLimitCount($param['keyWord'], $param['pid']);        $rs['list'] = self::$Model->getLimitList($param);        return $rs;    }    /**添加规则     * @param $apiObj api对象     * @return int 成功返回0，失败返回1，标识重复返回2，     */    public function addMenu($apiObj)    {        $param = get_object_vars($apiObj);        //检查规则标识是否重复，重复返回2        $r = self::$Model->checkRepeat($param['name']);        if ($r)            return 2;        //成功返回0，失败返回2        $r = self::$Model->addMenu($param);        return $r == true ? 0 : 1;    }    /**修改规则     * @param $apiObj     * @return int 成功返回0，失败返回1，名称重复返回2     */    public function editMenu($apiObj)    {        $param = get_object_vars($apiObj);        //检查名称重复，重复返回2        $r = self::$Model->checkRepeat($param['name'], $param['id']);        if ($r){            return 2;        }        $r = self::$Model->editMenu($param['id'], $param);        if ($r == true) {            $id = $param['id'];            addLog("编辑菜单，编号：$id");        }        return $r == true ? 0 : 1;    }    /** 删除规则     * @param $ids id列表 如1,2,3     * @return int     */    public function delMenu($ids)    {        $r = self::$Model->delMenu($ids);        if ($r) {            $ids = json_encode($ids);            addLog("删除菜单，编号：$ids");        }        return $r == true ? 0 : 1;    }    /** 获取单个规则信息     * @param $id     * @return mixed     */    public function getInfo($id)    {        $r = self::$Model->getInfo($id);        return $r;    }    public function getMenusInGroups($gids)    {        if (\PhalApi\DI()->cache === null) {            $menus = self::$Model->getMenusInGroups($gids);        } else {            $menus = self::$Model->getMenusInGroupsCache($gids);        }        return $menus;    }}