<?phpnamespace App\Auth\Domain;use  App\Auth\Model\User as ModelUser;use  App\Auth\Domain\Group as ModelGroup;use  App\Auth\Domain\Rule as ModelRule;/** * 用户领域类 * * @author hms */class User{    private static $Model = null;    public function  __construct()    {        if (self::$Model == null) {            self::$Model = new ModelUser();        }    }        public function getUserInfo($uid) {        $r=self::$Model->getUserInfo($uid);        return $r;    }	//查找当前用户所拥有的组的权限 注admin不受控制	public function getRuleList($id,$user){		$rule=[];		if($user['admin_name']!='admin'){//管理员不受权限所控制			 $rule=self::$Model->select_admin_auth_rule();		}else{			$groupid=$user['admin_auth_group_id'];			//根据分组去查用户所拥有权限			$ModelGroup=new ModelGroup();			$group_data=$ModelGroup->getGroupOne($groupid);			$auth_group_rules=$group_data['auth_group_rules'];			$moduleRule=new ModelRule();			$rule=$moduleRule->getRulesInGroups($auth_group_rules);		}		return $rule;	} }